import { notification, Typography } from "antd";
import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";
import ReadDataTable from "../../../components/Common/ReadDataTable";
import { getAssessment } from "../../../services/assessmentService";

const { Text, Title } = Typography;

const formatData = (data) => {
  return {
    general: [
      {
        label: "Title:",
        text: data.title,
      },
      {
        label: "Description:",
        text: data.description,
      },
    ],
    sections: [
      { label: "Price:", text: data.price ?? "" },
      { label: "Currency:", text: data.currency ?? "" },
    ],
  };
};

const AssessmentPage = (props) => {
  const [assessment, setAssessment] = useState(null);
  const [formattedData, setFormattedData] = useState({});
  const assessmentId = props.match.params.id;

  const getAssessmentData = async () => {
    try {
      const result = await getAssessment({ id: assessmentId });
      setAssessment(result.data);
      let data = formatData(result.data);
      setFormattedData(data);
    } catch (error) {
      notification["error"]({
        message: "Error",
        description: `Error: ${error.message}`,
      });
    }
  };

  useEffect(() => {
    getAssessmentData();
  }, []);
  return (
    <>
      <section className="grey-section">
        <Title level={2}>Assessment details.</Title>
        <ReadDataTable items={formattedData.general} />

        {/* <Divider />
        <Title level={5}>Cost</Title>
        <ReadDataTable items={formattedData.costs} /> */}
      </section>
    </>
  );
};

export default withRouter(AssessmentPage);
