import moment from "moment";
import React from "react";
import ReactHtmlParser from "react-html-parser";

const ReleaseNotesSection = ({ releaseNote }) => {
  if (!releaseNote) return null;
  const { releaseDate, title, summary, customHTML } = releaseNote;
  const dateFormat = "dddd, Do MMMM YYYY";
  const dateString = moment(releaseDate).format(dateFormat);
  return (
    <div>
      <h2>{title}</h2>
      <p>{summary}</p>
      {customHTML?.length > 0 ? <div>{ReactHtmlParser(customHTML)}</div> : null}
    </div>
  );
};

export default ReleaseNotesSection;
