export const dayOfWeekMap = {
  Mon: 0,
  Tues: 1,
  Wed: 2,
  Thurs: 3,
  Fri: 4,
  Sat: 5,
  Sun: 6,
};

export const conditionalOptions = [
  {
    label: "true",
    value: true,
  },
  {
    label: "false",
    value: false,
  },
];

export const type = {
  NUMBER: "Number",
  STRING: "String",
  DATE: "Date",
};

export const GENERIC_ERROR_MESSAGE =
  "Getting response from the backend failed!";

export const IDLE_STATUS_MESSAGE = "In idle mode";
export const SUCCESS_MESSAGE = "Successful!";

// state status
export const IDLE = "IDLE";
export const ERROR = "ERROR";
export const SUCCESS = "SUCCESS";
export const REQUEST_RETRACT_SUCCESS = "REQUEST_RETRACT_SUCCESS";
