import { Col, Divider, notification, Row, Tag, Typography } from "antd";
import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";
import { getAssessment } from "../../../services/assessmentService";

const { Text, Title } = Typography;

const renderBoolean = (bool) => {
  const rendered =
    bool === true
      ? { text: "Yes", color: "green" }
      : { text: "No", color: "volcano" };

  return <Tag color={rendered.color}>{rendered.text}</Tag>;
};

const renderSections = ({ fieldName, fieldValue }) => {
  if (typeof fieldValue === "boolean")
    return (
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Text strong>{fieldName}</Text>
        </Col>
        <Col span={18}>{renderBoolean(fieldValue)}</Col>
      </Row>
    );
  if (typeof fieldValue === "string" || typeof fieldValue === "number")
    return (
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Text strong>{fieldName}</Text>
        </Col>
        <Col span={18}>{fieldValue}</Col>
      </Row>
    );
};

const renderFields = ({ fieldName, fieldValue }) => {
  if (typeof fieldValue === "boolean")
    return (
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Text strong>{fieldName}</Text>
        </Col>
        <Col span={18}>{renderBoolean(fieldValue)}</Col>
      </Row>
    );
  if (typeof fieldValue === "string" || typeof fieldValue === "number")
    return (
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Text strong>{fieldName}</Text>
        </Col>
        <Col span={18}>{fieldValue}</Col>
      </Row>
    );

  // if (typeof fieldValue === "object" && Array.isArray(fieldValue))
  //   return (
  //     <Row gutter={[16, 16]}>
  //       <Col span={6}>
  //         <Text strong>{fieldName}</Text>
  //       </Col>
  //       <Col span={18}>
  //         <List
  //           size="small"
  //           bordered
  //           dataSource={fieldValue}
  //           renderItem={(item) => <List.Item>{JSON.stringify(item)}</List.Item>}
  //         />
  //       </Col>
  //     </Row>
  //   );
  if (fieldValue == "sections") {
    return renderSections();
  }
};

const AssessmentPage = (props) => {
  const [assessment, setAssessmentId] = useState(null);
  const [assessmentKeys, setAssessmentKeys] = useState(null);
  const assessmentId = props.match.params.id;

  const getAssessmentData = async () => {
    try {
      const result = await getAssessment({ id: assessmentId });
      setAssessmentId(result.data);
      const keys = Object.keys(result.data);
      setAssessmentKeys(keys);
    } catch (error) {
      notification["error"]({
        message: "Error",
        description: `Error: ${error.message}`,
      });
    }
  };

  useEffect(() => {
    getAssessmentData();
  }, []);
  return (
    <>
      <Title level={2}>Heres the assessment details.</Title>

      <Text style={{ paddingLeft: "15px", paddingRight: "5px" }}>isActive</Text>
      <Divider />

      {assessmentKeys &&
        assessmentKeys?.map((fieldName) =>
          renderFields({
            fieldName,
            fieldValue: assessment?.[fieldName],
          })
        )}
    </>
  );
};

export default withRouter(AssessmentPage);
