const renderFields = ({ fieldName, fieldValue }) => {
  if (typeof fieldValue === "boolean")
    return (
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Text strong>{fieldName}</Text>
        </Col>
        <Col span={18}>{renderBoolean(fieldValue)}</Col>
      </Row>
    );
  if (typeof fieldValue === "string" || typeof fieldValue === "number")
    return (
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Text strong>
            {fieldName.replace(/(\w)(\w*)/g, function (g0, g1, g2) {
              return g1.toUpperCase() + g2.toLowerCase();
            })}
          </Text>
        </Col>
        <Col span={18}>{fieldValue}</Col>
      </Row>
    );

  if (typeof fieldValue === "object" && Array.isArray(fieldValue)) {
    if (fieldName == "sections") {
      assessmentKeys &&
        assessmentKeys?.map((fieldName) =>
          renderFields({
            fieldName,
            fieldValue: assessment?.[fieldName],
          })
        );
    }
