    //       renderItem={(item) => <List.Item>{JSON.stringify(item)}</List.Item>}
    //     />
    //   </Row>
    // );
  }
};

const AssessmentPage = (props) => {
  const [assessment, setAssessmentId] = useState(null);
  const [assessmentKeys, setAssessmentKeys] = useState(null);
  const assessmentId = props.match.params.id;

  const getAssessmentData = async () => {
    try {
      const result = await getAssessment({ id: assessmentId });
      setAssessmentId(result.data);
      const keys = Object.keys(result.data);
      setAssessmentKeys(keys);
    } catch (error) {
      notification["error"]({
        message: "Error",
        description: `Error: ${error.message}`,
      });
    }
  };

  useEffect(() => {
    getAssessmentData();
  }, []);
  return (
    <>
      <Title level={2}>Heres the assessment details.</Title>

      <Divider />

      {assessmentKeys &&
        assessmentKeys?.map((fieldName) =>
          renderFields({
            fieldName,
            fieldValue: assessment?.[fieldName],
          })
        )}
    </>
  );
};

export default withRouter(AssessmentPage);
